import React from 'react';
import PropTypes from 'prop-types';
import DefaultListComponent from '../default-component/default-list';

/**
 * @description Renders a list that has a link label that removes itself on click
 * @prop classNames {array} Additional classnames to be added to the component
 * @prop remove {function} function generated by component tree class that removes the component from it's parent
 */

export default class ListRemovableSingle extends DefaultListComponent {

	static defaultProps = {
		classNames: ['schema__list', 'schema__list__removable-single'],
		isButton : false
	};

	static propTypes = {
		classNames: PropTypes.array,
		remove: PropTypes.func
	};

	renderLabel() {
		return (
			<div className="clearfix schema__list__removable__link__container">
				<button className="schema__list__removable__link button--text"
						aria-label="Remove"
						onClick={this.props.remove}
						data-tealium-narrative ="Remove" >
					{this.props.isButton ? 'Delete' : <i className="schema__icon pe-7s-close"/> }
					<span className="internal-tooltip-content" />
				</button>
			</div>
		);
	}

}


